mapper.select:
SELECT COUNT(*)
FROM month_category_money
WHERE user_id = #{userId} AND YEAR(`current_date`) = #{year} AND MONTH(`current_date`) = #{month}

SELECT mc.id, user_id, category_id, content, current_money, total_money, `current_date`
FROM month_category_money as mc
    LEFT JOIN category_big as cb
        ON mc.category_id = cb.id
WHERE user_id = #{userId}
  AND YEAR(`current_date`) = #{year}
  AND MONTH(`current_date`) = #{month}
  AND `type` NOT IN ('수입', '저금');
